@pytest.fixture(scope="session")
def ens_setup():
    ens = ENS(EthereumTesterProvider())

    # get a copy of eth accounts
    ens.web3.middleware_stack.remove('stalecheck')
    accounts = ens.web3.eth.accounts

    # wrap web3 in mocking layer
    w3 = Mock(wraps=ens.web3)
    ens.web3 = w3

    # ** Set up ENS contracts **

    # create ENS contract
    ens_key = accounts[1]  # use alt account so test transactions don't have write access
    eth_labelhash = w3.toBytes(hexstr=w3.sha3(text='eth'))
    eth_namehash = w3.toBytes(hexstr='0x93cdeb708b7545dc668eb9280176169d1c33cfd8ed6f04690a0bcc88a93fc4ae')  # noqa: 501
    resolver_namehash = w3.toBytes(hexstr='0xfdd5d5de6dd63db72bbc2d487944ba13bf775b50a80805fe6fcaba9b0fba88f5')  # noqa: 501
    ens_contract = deploy(w3, ENSFactory(w3), ens_key)

    # create public resolver
    public_resolver = deploy(w3, PublicResolverFactory(w3), ens_key, args=[ens_contract.address])

    # set 'resolver.eth' to resolve to public resolver
    ens_contract.transact({'from': ens_key}).setSubnodeOwner(
        b'\0' * 32,
        eth_labelhash,
        ens_key
    )
    ens_contract.transact({'from': ens_key}).setSubnodeOwner(
        eth_namehash,
        w3.toBytes(hexstr=w3.sha3(text='resolver')),
        ens_key
    )
    public_resolver.transact({'from': ens_key}).setAddr(resolver_namehash, public_resolver.address)

    # create .eth auction registrar
    eth_registrar = deploy(
        w3,
        ETHRegistrarFactory(w3),
        ens_key,
        args=[ens_contract.address, eth_namehash, 0],
    )

    # set 'eth' to resolve to registrar
    public_resolver.transact({'from': ens_key}).setAddr(eth_namehash, eth_registrar.address)

    # make the registrar the owner of the 'eth' name
    ens_contract.transact({'from': ens_key}).setSubnodeOwner(
        b'\0' * 32,
        eth_labelhash,
        eth_registrar.address
    )

    w3.middleware_stack.add(make_stalecheck_middleware(86400), 'stalecheck')
    return ens


@pytest.fixture
def ens(ens_setup, mocker):
    mocker.patch('web3.middleware.stalecheck._isfresh', return_value=True)
    return ens_setup


@pytest.fixture
def registrar(ens, monkeypatch, addr9):
    monkeypatch.setattr(ens, 'owner', lambda namehash: addr9)
    return ens.registrar


@pytest.fixture
def fake_hash_hexout():
    def _fake_hash(tohash=None, text=None):
        if text:
            tohash = text.encode('utf-8')
        assert isinstance(tohash, bytes)
        tohash = b'b' + tohash
        hash_bytes = b'HASH(%s)' % tohash
        return Web3.toHex(hash_bytes)
    return _fake_hash


@pytest.fixture
def fake_hash(fake_hash_hexout):
    return lambda name: Web3.toBytes(hexstr=fake_hash_hexout(name))


@pytest.fixture
def fake_hash_utf8(fake_hash):
    return lambda name: fake_hash(name.encode())
